 ZhihuLinkGetRaw::usage = "";ZhihuLinkGet::usage = "";Begin["`Private`"];Needs["GeneralUtilities`"];$APIURL = <|   "Miscellaneous" -> <|     "Scheme" -> "https",     "Domain" -> "www.zhihu.com",               "Profile" -> <|"Path" -> StringTemplate["api/v4/members/`id`"],        "RequireAuth" -> False|>,               "Topic" -> <|"Path" -> StringTemplate["api/v4/topics/`id`"],        "RequireAuth" -> False|>,               "Question" -> <|       "Path" -> StringTemplate["api/v4/questions/`id`"],        "RequireAuth" -> False|>,               "Answer" -> <|"Path" -> StringTemplate["api/v4/answers/`id`"],        "RequireAuth" -> False|>,               "Messages" -> <|"Path" -> StringTemplate["api/v4/messages"],        "RequireAuth" -> True|>,               "Notifications" -> <|       "Path" -> StringTemplate["api/v4/default-notifications"],        "RequireAuth" -> True|>     |>,   "Members" -> <|     "Scheme" -> "https",     "Domain" -> "www.zhihu.com",               "Followees" -> <|       "Path" -> StringTemplate["api/v4/members/`id`/followees"],        "RequireAuth" -> False|>,               "Followers" -> <|       "Path" -> StringTemplate["api/v4/members/`id`/followers"],        "RequireAuth" -> False|>,               "FollowingQuestions" -> <|       "Path" ->         StringTemplate["api/v4/members/`id`/following-questions"],        "RequireAuth" -> False|>,               "FollowingTopics" -> <|       "Path" ->         StringTemplate[         "api/v4/members/`id`/following-topic-contributions"],        "RequireAuth" -> False|>,               "FollowingColumns" -> <|       "Path" ->         StringTemplate["api/v4/members/`id`/following-columns"],        "RequireAuth" -> False|>,               "FollowingFavlists" -> <|       "Path" ->         StringTemplate["api/v4/members/`id`/following-favlists"],        "RequireAuth" -> False|>,               "Questions" -> <|       "Path" -> StringTemplate["api/v4/members/`id`/questions"],        "RequireAuth" -> False|>,               "Answers" -> <|       "Path" -> StringTemplate["api/v4/members/`id`/answers"],        "RequireAuth" -> False|>,               "Pins" -> <|"Path" -> StringTemplate["api/v4/members/`id`/pins"],        "RequireAuth" -> False|>,               "Articles" -> <|       "Path" -> StringTemplate["api/v4/members/`id`/articles"],        "RequireAuth" -> False|>,               "Columns" -> <|       "Path" ->         StringTemplate["api/v4/members/`id`/column-contributions"],        "RequireAuth" -> False|>,               "Favlists" -> <|       "Path" -> StringTemplate["api/v4/members/`id`/favlists"],        "RequireAuth" -> False|>,               "Activities" -> <|       "Path" -> StringTemplate["api/v4/members/`id`/activities"],        "RequireAuth" -> True|>     |>,   "Questions" -> <|     "Scheme" -> "https",     "Domain" -> "www.zhihu.com",               "Followers" -> <|       "Path" ->         StringTemplate["api/v4/questions/`id`/concerned_followers"],        "RequireAuth" -> False|>,               "Comments" -> <|       "Path" -> StringTemplate["api/v4/questions/`id`/comments"],        "RequireAuth" -> False|>,          "Invitees" -> <|       "Path" -> StringTemplate["api/v4/questions/`id`/invitees"],        "RequireAuth" -> False|>,          "InvitationCandidates" -> <|       "Path" ->         StringTemplate["api/v4/questions/`id`/invitation-candidates"],        "RequireAuth" -> False|>     |>,   "Answers" -> <|     "Scheme" -> "https",     "Domain" -> "www.zhihu.com",               "Upvoters" -> <|       "Path" ->         StringTemplate["api/v4/answers/`id`/concerned_upvoters"],        "RequireAuth" -> False|>,               "Comments" -> <|       "Path" -> StringTemplate["api/v4/answers/`id`/comments"],        "RequireAuth" -> False|>     |>,   "Pins" -> <|     "Scheme" -> "https",     "Domain" -> "www.zhihu.com",               "Comments" -> <|       "Path" -> StringTemplate["api/v4/pins/`id`/comments"],        "RequireAuth" -> False|>     |>,   "Topics" -> <|     "Scheme" -> "https",     "Domain" -> "www.zhihu.com",               "Followers" -> <|       "Path" -> StringTemplate["api/v4/topics/`id`/followers"],        "RequireAuth" -> False|>     |>,   "Articles" -> <|     "Scheme" -> "https",     "Domain" -> "zhuanlan.zhihu.com",               "Upvoters" -> <|       "Path" -> StringTemplate["api/posts/`id`/likers"],        "RequireAuth" -> False|>,               "Comments" -> <|       "Path" -> StringTemplate["api/posts/`id`/comments"],        "RequireAuth" -> False|>     |>,   "Columns" -> <|     "Scheme" -> "https",     "Domain" -> "zhuanlan.zhihu.com",               "Followers" -> <|       "Path" -> StringTemplate["api/columns/`id`/followers"],        "RequireAuth" -> False|>,               "Posts" -> <|"Path" -> StringTemplate["api/columns/`id`/posts"],        "RequireAuth" -> False|>     |>   |>;ZhihuLinkGetInit[] :=  Module[{},   $ZhihuCookie = Import[FindFile["zhihu.cookie"]];    $ZhihuAuth = Import[FindFile["zhihu.auth"]];   ];   ExportJSON[cat_String, item_String, name_String, content_] :=   Module[{path = FileNameJoin[{cat, item}]},   If[! DirectoryQ[path], CreateDirectory[path]];    Export[FileNameJoin[{path, name <> ".json"}], content]  ];FixURL[url_String, "Members", "FollowingQuestions"] :=   "https://www.zhihu.com/api/v4" <> StringDrop[url, 21];  FixURL[url_String, _, _] := url;ZhihuLinkGetRaw[cat_String, item_String, id_String, offset_: 0, limit_: 20] :=   GeneralUtilities`ToAssociations[   URLExecute[    HTTPRequest[<|      "Scheme" -> $APIURL[cat]["Scheme"],      "Domain" -> $APIURL[cat]["Domain"],      "Headers" -> {"Cookie" -> $ZhihuCookie,             "auth" -> If[$APIURL[cat][item]["RequireAuth"], $ZhihuAuth, Nothing]},            "Path" -> {Evaluate[$APIURL[cat][item]["Path"][<|"id" -> id|>]]},            "Query" -> {"limit" -> limit, "offset" -> offset}      |>],    Authentication -> None]  ];  ZhihuLinkGetRaw[url_String, requireAuthQ_: False] :=   GeneralUtilities`ToAssociations[URLExecute[HTTPRequest[url,     <|"Headers" -> {        "Cookie" -> $ZhihuCookie,        "auth" -> If[requireAuthQ, $ZhihuAuth, Nothing]}      |>],    Authentication -> None]  ];  ZhihuLinkGet[cat_String, item_String, name_String] :=  Module[{current, data = {}},         If[! (KeyExistsQ[$APIURL, cat] && KeyExistsQ[$APIURL[cat], item]),     Return[$Failed]];   current = ZhihuLinkGetRaw[cat, item, name];   If[! KeyExistsQ[current, "paging"],            ExportJSON[cat, item, name, current],            data = Join[data, current["data"]];    While[! current["paging"]["is_end"],     current =       ZhihuLinkGetRaw[FixURL[current["paging"]["next"], cat, item]];     data = Join[data, current["data"]];     ];    ExportJSON[cat, item, name, data]    ]   ];End[];